function getCanvasContext(){var e=document.querySelector("#canvas");return e?(ctx=e.getContext("2d"),ctx):null}function drawSphere(e){e.strokeStyle="#eeeeee",e.beginPath(),e.arc(400,300,200,0,2*Math.PI,!0),e.stroke(),e.scale(.5,1),e.arc(400,300,200,0,2*Math.PI,!0),e.stroke()}document.addEventListener("DOMContentLoaded",function(){var e=getCanvasContext();e?drawSphere(e):console.error("Unable to start canvas drawing ☹️.")});